const { ObjectId } = mongoose.Types;

       const getReviewById = async (ctx, next) => {
  const { id } = ctx.params;
  if (!ObjectId.isValid(id)) {
    ctx.status = 400;
    return;
  }
  try {
    const review = await Review.findById(id);
    if (!review) {
      ctx.status = 404;
      return;
    }
    ctx.state.review = review;
    return next();
  } catch (e) {
    ctx.throw(500, e);
  }
};

       const write = async (ctx) => {
  const { text, videoId } = ctx.request.body;
  const review = new Review({
    text,
    videoId,
    user: ctx.state.user,
  });
  try {
    await review.save();
    ctx.body = review;
  } catch (e) {
    ctx.throw(500, e);
  }
};

       const list = async (ctx) => {
  const page = parseInt(ctx.query.page || '1', 10);

  if (page < 1) {
    ctx.status = 400;
    return;
  }
  const { videoId } = ctx.query;
  const query = {
    ...(videoId ? { videoId: videoId } : {}),
  };
  try {
    const reviews = await Review.find(query)
      .sort({ _id: -1 })
      .limit(20)
      .skip((page - 1) * 10)
      .lean()
      .exec();
    const reviewCount = await Review.countDocuments(query).exec();
    ctx.set('Last-Page', Math.ceil(reviewCount / 10));
    ctx.body = reviews.map((review) => ({
      ...review,
      text:
        review.text.length < 200
          ? review.text
          : `${review.text.slice(0, 200)}...`,
    }));
  } catch (e) {
    ctx.throw(500, e);
  }
};

       const remove = async (ctx) => {
  const { id } = ctx.params;
  try {
    await Review.findByIdAndRemove(id).exec();
    ctx.status = 204;
  } catch (e) {
    ctx.throw(500, e);
  }
};

       const checkOwnVideo = (ctx, next) => {
  const { user, video } = ctx.state;
  if (video.user.username !== user._id) {
    ctx.status = 403;
    return;
  }
  return next();
};
